    weather_data = msg.payload[0].hourly;
    waves_data = msg.payload[1].hourly;
    latitude = msg.payload[1].latitude;
    longitude = msg.payload[1].longitude;
} else {
    weather_data = msg.payload[1].hourly;
    waves_data = msg.payload[0].hourly;
    latitude = msg.payload[0].latitude;
    longitude = msg.payload[0].longitude;
}

var systemDateTime = new Date();

for (var i = 0; i < weather_data.time.length; i++) {  

    var currentDateTime = new Date(weather_data.time[i]);
    if (currentDateTime < systemDateTime) {
        continue;
    }

    msg.payload = {};

    msg.payload.latitude = latitude;
    msg.payload.longitude = longitude;

    msg.payload.temperature = weather_data.temperature_2m[i];
    msg.payload.humidity = weather_data.relative_humidity_2m[i];
    msg.payload.rain = weather_data.rain[i];
    msg.payload.showers = weather_data.showers[i];
    msg.payload.snowfall = weather_data.snowfall[i];
    msg.payload.cloud_cover = weather_data.cloud_cover[i];
    msg.payload.wind_speed = weather_data.wind_speed_10m[i];

    msg.payload.wave_height = waves_data.wave_height[i];
    msg.payload.wave_direction = waves_data.wave_direction[i];
    msg.payload.wave_period = waves_data.wave_period[i];

    msg.payload.timestamp = weather_data.time[i];

    node.send(msg);
}

