var temperature_clusters = global.get('piraeusTemperatureClusters');
var rain_clusters = global.get('piraeusRainClusters');
var wind_speed_clusters = global.get('piraeusWindSpeedClusters');
var wave_height_clusters = global.get('piraeusWaveHeightClusters');

var name = "Piraeus"

var diff;
var min;

min = 10000000;
var overall_temperature_cluster = -1;
for (var k = 0; k < temperature_clusters.length;k++) {
    diff = Math.abs(temperature_clusters[k].centroid[0] - temperature);
    if (diff<min) {
        overall_temperature_cluster = k;
        min = diff;
    }
}

min = 10000000;
var overall_rain_cluster = -1;
for (var k = 0; k < rain_clusters.length; k++) {
    diff = Math.abs(rain_clusters[k].centroid[0] - rain);
    if (diff < min) {
        overall_rain_cluster = k;
        min = diff;
    }
}

min = 10000000;
var overall_wind_speed_cluster = -1;
for (var k = 0; k < wind_speed_clusters.length; k++) {
    diff = Math.abs(wind_speed_clusters[k].centroid[0] - wind_speed);
    if (diff < min) {
        overall_wind_speed_cluster = k;
        min = diff;
    }
}

min = 10000000;
var overall_wave_height_cluster = -1;
for (var k = 0; k < wave_height_clusters.length; k++) {
    diff = Math.abs(wave_height_clusters[k].centroid[0] - wave_height);
    if (diff < min) {
        overall_wave_height_cluster = k;
        min = diff;
    }
}

var temperatureState;
switch (overall_temperature_cluster) {
    case 0:
        temperatureState = "bad state"
        break;
    case 1:
        temperatureState = "moderate state"
        break;
    case 2:
        temperatureState = "good state"
        break;
    case 3:
        temperatureState = "excellent state"
        break;
    default:
        temperatureState = "unknown state"
}

var rainState;
switch (overall_rain_cluster) {
    case 0:
        rainState = "bad state"
        break;
    case 1:
        rainState = "moderate state"
        break;
    case 2:
        rainState = "good state"
        break;
    case 3:
        rainState = "excellent state"
        break;
    default:
        rainState = "unknown state"
}

var windSpeedState;
switch (overall_wind_speed_cluster) {
    case 0:
        windSpeedState = "bad state"
        break;
    case 1:
        windSpeedState = "moderate state"
        break;
    case 2:
        windSpeedState = "good state"
        break;
    case 3:
        windSpeedState = "excellent state"
        break;
    default:
        windSpeedState = "unknown state"
}

var waveHeightState;
switch (overall_wave_height_cluster) {
    case 0:
        waveHeightState = "bad state"
        break;
    case 1:
        waveHeightState = "moderate state"
        break;
    case 2:
        waveHeightState = "good state"
        break;
    case 3:
        waveHeightState = "excellent state"
        break;
    default:
        waveHeightState = "unknown state"
}

var states = [temperatureState, rainState, windSpeedState, waveHeightState];

var statePriority = {
    "bad state": 3,
    "moderate state": 2,
    "good state": 1,
    "excellent state": 0
};

var priorityColor = {
    3: "red",
    2: "orange",
    1: "green",
    0: "blue"
};

var highestPriority = -1;
for (var i = 0; i < states.length; i++) {
    var currentState = states[i];
    if (statePriority.hasOwnProperty(currentState)) {
        var currentPriority = statePriority[currentState];
        if (currentPriority > highestPriority) {
            highestPriority = currentPriority;
        }
    }
}

var iconColor = "grey";
iconColor = priorityColor[highestPriority]

msg.payload = {};
msg.payload.name = name;
msg.payload.lat = lat;
msg.payload.lon = lon;
msg.payload.timestamp = timestamp;
msg.payload.iconColor = iconColor;
msg.payload.layer = "location";

